package kamal;

public class RecFactorial {

	public static void main(String[] args) {
		RecFactorial f=new RecFactorial();
		f.factorial(5);
		System.out.println(fact);
		// TODO Auto-generated method stub

	}
static int fact=1,a=1;
	private void factorial(int i) {
		if(a<=i)
		{
			fact=fact*a;
			a++;
			factorial(5);
		}
		
		// TODO Auto-generated method stub
		
	}
	 

}
