package kamal;

public class pattern1 {

	public static void main(String[] args) {
		int r=0,x=4,y=3;
		char a='A';
		while(r<y)
		{
			int c=0;
			while(c<x)
			{
				System.out.print(a+" ");
				a++;
				c++;
			}
			System.out.println();
			r++;
		}
		// TODO Auto-generated method stub

	}

}
