package kamal;

public class pattern2 {

	public static void main(String[] args) {
		int i=1,x=1,r=3,c=3;
		while(i<=r)
		{
			int j=1;
			while(j<=c)
			{
				System.out.print(x+" ");
				x++;
				j++;
			}
			System.out.println();
			i++;
		}
		// TODO Auto-generated method stub

	}

}
